a.t-attf-onclick:
(function(){ document.getElementById('month_1').style.display = 'block'; document.getElementById('month_2').style.display = 'none';
                                                                                var nexts = document.getElementsByClassName('btn_next'); 
                                                                                for (var i=0;i<nexts.length;i+=1){
                                                                                    nexts[i].style.display = 'inline-block';
                                                                                }
                                                                                var previous = document.getElementsByClassName('btn_previous');
                                                                                for (var i=0;i<previous.length;i+=1){
                                                                                    previous[i].style.display = 'none';
                                                                                }})();return false;

(function(){ document.getElementById('month_1').style.display = 'none'; document.getElementById('month_2').style.display = 'block';
                                                                                var nexts = document.getElementsByClassName('btn_next'); 
                                                                                for (var i=0;i<nexts.length;i+=1){
                                                                                    nexts[i].style.display = 'none';
                                                                                }
                                                                                var previous = document.getElementsByClassName('btn_previous');
                                                                                for (var i=0;i<previous.length;i+=1){
                                                                                    previous[i].style.display = 'inline-block';
                                                                                }})();return false;
